import { useState } from "react";

const navItems = [
  { label: "首頁", href: "#home" },
  { label: "學歷", href: "#education" },
  { label: "個人介紹", href: "#about" },
  { label: "作品介紹", href: "#works" },
  { label: "聯絡我", href: "#contact", cta: true },
];

function Navbar() {
  const [navOpen, setNavOpen] = useState(false);

  const closeNav = () => setNavOpen(false);

  return (
    <nav
      className="navbar navbar-expand-lg navbar-light glass-nav sticky-top"
      aria-label="主要導覽"
    >
      <div className="container">
        <a
          className="navbar-brand brand d-flex align-items-center gap-3"
          href="#home"
          onClick={closeNav}
        >
          <span className="brand-mark">Yu</span>
          <span className="brand-text d-none d-sm-inline">
            <strong>Ping-Yen, Yu</strong>
            <span>個人介紹網站</span>
          </span>
        </a>
        <button
          className="navbar-toggler"
          type="button"
          aria-controls="mainNav"
          aria-expanded={navOpen}
          aria-label="切換導覽"
          onClick={() => setNavOpen((open) => !open)}
        >
          <span className="navbar-toggler-icon"></span>
        </button>
        <div
          className={`collapse navbar-collapse ${navOpen ? "show" : ""}`}
          id="mainNav"
        >
          <ul className="navbar-nav ms-auto mb-2 mb-lg-0 align-items-lg-center gap-lg-2">
            {navItems.map((item) => (
              <li
                className={`nav-item ${item.cta ? "ms-lg-2" : ""}`}
                key={item.href}
              >
                <a
                  className={item.cta ? "btn btn-primary nav-cta" : "nav-link"}
                  href={item.href}
                  onClick={closeNav}
                >
                  {item.label}
                </a>
              </li>
            ))}
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default Navbar;
