import { useLanguage } from "../context/LanguageContext";

function Education() {
  const { content, language } = useLanguage();
  const { education } = content;
  const isEnglish = language === 'en';

  return (
    <section
      className="section section-muted py-5 py-lg-6 fade-up delay-1"
      id="education"
    >
      <div className="container">
        <div className="row align-items-end g-4 section-header">
          <div className="col-lg-7">
            <p className="section-eyebrow">{education.eyebrow}</p>
            <h2>{education.title}</h2>
          </div>
          <div className="col-lg-5">
            <p className="section-description">{education.description}</p>
          </div>
        </div>
        <div className="row g-4 mt-1">
          {education.items.map((item) => (
            <div className="col-md-6" key={item.year}>
              <div className="card timeline-card h-100 border-0">
                <div className="card-body">
                  <div className="timeline-year">{item.year}</div>
                  <h3>{isEnglish ? item.school_En || item.school : item.school}</h3>
                  {item.school_En && !isEnglish && (
                    <p className="text-muted education-translation">
                      {item.school_En}
                    </p>
                  )}
                  {isEnglish ? (
                    item.description_En && (
                      <p className="text-muted">{item.description_En}</p>
                    )
                  ) : (
                    item.description && (
                      <p className="text-muted">{item.description}</p>
                    )
                  )}
                  {item.description_En && !isEnglish && (
                    <p className="text-muted education-translation">
                      {item.description_En}
                    </p>
                  )}
                  <div className="d-flex flex-wrap gap-2">
                    {item.tags.map((tag) => (
                      <span className="badge rounded-pill" key={tag}>
                        {tag}
                      </span>
                    ))}
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
}

export default Education;
