const en = {
  nav: {
    brandName: "Ping-Yen Yu",
    brandTagline: "Personal Portfolio",
    languageLabel: "Language switch",
    ariaLabel: "Primary navigation",
    toggleLabel: "Toggle navigation",
    items: [
      { label: "Home", href: "#home" },
      { label: "Education", href: "#education" },
      { label: "About", href: "#about" },
      { label: "Projects", href: "#works" },
      { label: "Contact", href: "#contact", cta: true },
    ],
  },
  hero: {
    eyebrow: "AI Software Engineer",
    headline: {
      prefix: "Hi, I'm",
      name: "Ping-Yen Yu",
      connector: ",",
    },
    lead: "Background in machine learning and deep learning, with strengths in LLMs, NLP, and explainable AI.",
    ctas: {
      primary: "View Projects",
      secondary: "Work Together",
    },
    stats: [
      { value: "2", label: "Projects" },
      { value: "1", label: "Partners" },
      { value: "1", label: "Award Nominations" },
    ],
    card: {
      status: "Open to work",
      location: "Taipei",
      name: "Ping-Yen Yu",
      title: "AI-Software Engineer",
      tags: [
        "LLM",
        "NLP",
        "Explainable AI",
        "Full-stack Integration",
        "Model Deployment",
      ],
    },
    floating: "AI Software Engineer",
    imageAlt: "Profile photo",
  },
  education: {
    eyebrow: "Education",
    title: "Learning Journey.",
    // description:
    //   "Balancing design and engineering, from user research to frontend delivery.",
    items: [
      {
        year: "2023 - 2025",
        school:
          "National Chi Nan University・Department of Computer Science and Information Engineering",
        logos: [
          { src: "/logo2.png", alt: "National Chi Nan University logo" },
          { src: "/70_70.gif", alt: "National Chi Nan University emblem" },
        ],
        degree: "Master's",
        description:
          "Paper：A Study on the Application of Explainable Information Retrieval Methods for Determining the Pathogenicity of Genetic Variants。",
        tags: [
          "LLM",
          "NLP",
          "Image Processing",
          "Image Recognition",
          "Explainable AI",
          "Cross-domain Research",
        ],
      },
      {
        year: "2019 - 2023",
        school:
          "Shih Chien University・Department of Information Technology and Communications",
        logos: [{ src: "/903635956.png", alt: "Shih Chien University logo" }],
        degree: "Bachelor's",
        // description:
        //   "Joined art and programming clubs, started bringing visual taste into web pages.",
        tags: ["Web Development", "Drone Implementation"],
      },
    ],
  },
  about: {
    eyebrow: "About",
    title: "Explain complexity through storytelling.",
    // description:
    //   "I turn abstract needs into tangible interactions that give products direction and brands a voice.",
    introTitle: "Bio",
    introParagraphs: [
      "I am an **application-driven AI Engineer** with a strong background in machine learning and deep learning, specializing in **LLMs, NLP, and explainable AI.**\n\nI have developed **medical and biomedical AI projects,** including biomedical literature analysis, medical information retrieval, and image recognition tasks, and have experience collaborating across interdisciplinary teams. I am familiar with the full implementation pipeline, from **data preprocessing and model training (fine-tuning) to inference deployment.**\n\nI have completed my master’s degree and military service, and I am currently seeking a **long-term full-time AI Engineer position.**",
    ],
    highlights: [
      {
        title: "Applied AI & Machine Learning",
        items: [
          "End-to-end Machine Learning / Deep Learning workflows, including data preprocessing, model training and evaluation, and inference deployment.\n\nHands-on experience with PyTorch and TensorFlow, and practical experience in optimizing models with performance–cost trade-offs.",
        ],
      },
      {
        title: "Medical & Biomedical AI",
        items: [
          "Experience in developing AI solutions for medical and biomedical applications, including literature analysis, information retrieval, and image recognition tasks.\n\nFamiliar with cross-domain collaboration environments and capable of handling domain-specific data.",
        ],
      },
      {
        title: "LLM & NLP Systems",
        items: [
          "Hands-on experience with large language models (LLMs) and natural language processing (NLP) applications, including transformer model fine-tuning, retrieval-augmented generation (RAG) workflow development, and chatbot building.\n\nExperience integrating LLM inference through APIs into practical systems.",
        ],
      },
      {
        title: "Front End Web Development & Productization",
        items: [
          "Front end experience ( React / Flask ). Able to productize research prototypes into usable web systems with end-to-end user flow.",
          "Experienced in integrating model inference into practical systems via APIs and delivering interpretable outputs.",
          "Developed an explainable literature assistance system that provides query capabilities, literature retrieval, prediction, and evidence sentence highlighting, with outputs designed for clinical/user scenarios.",
        ],
      },
    ],
  },
  works: {
    eyebrow: "Projects",
    title: "A few representative projects.",
    description: "Focused on brand tone, narrative, and interaction rhythm.",
    items: [
      {
        year: "2024",
        type: "Brand Website",
        title: "Morning Light Coffee Website",
        description:
          "Built a warm, clean visual and interaction flow to improve online ordering conversion.",
        tags: ["Visual Design", "React", "Dynamic Navigation"],
      },
      {
        year: "2023",
        type: "Landing Page",
        title: "Cloud Notes Product Landing",
        description:
          "Designed content structure and story rhythm to improve trial conversion for new features.",
        tags: [
          "Information Architecture",
          "Interactive Prototype",
          "Marketing Assets",
        ],
      },
      {
        year: "2022",
        type: "Interactive Exhibit",
        title: "City Energy-Saving Showcase",
        description:
          "Visualized energy-saving results with data storytelling and touch interactions.",
        tags: ["Data Visualization", "Interactive Media", "UI Design"],
      },
    ],
  },
  contact: {
    eyebrow: "Contact",
    title: "Let's build something meaningful.",
    description:
      "If you have an idea or collaboration in mind, leave a message and I'll get back to you.",
    quickTitle: "Quick Contact",
    quickDescription:
      "Currently open to brand sites, product UI, and interactive projects, supporting from concept to launch.",
    labels: {
      email: "Email",
      github: "GitHub",
      linkedin: "LinkedIn",
      phone: "Phone",
    },
    form: {
      labels: {
        name: "Your Name",
        email: "Email",
        message: "Message",
      },
      placeholders: {
        name: "Jane Doe",
        email: "hello@email.com",
        message: "Tell me about your idea or project",
      },
      submit: "Send Message",
      note: "A draft email will open. Please review and send.",
      empty: "(not provided)",
      separator: ": ",
      subject: "Website Contact Form",
      visitor: "Visitor",
    },
  },
  footer: {
    copyright: "© 2025 Ping-Yen Yu. All rights reserved.",
    backToTop: "Back to top",
  },
};

export default en;
