import { useEffect, useMemo, useState } from "react";
import { supabase } from "../lib/supabaseClient";
import localContent from "../content";
import { LanguageProvider } from "../context/LanguageContext";
import Navbar from "../components/Navbar";
import Hero from "../components/Hero";
import Education from "../components/Education";
import Experience from "../components/Experience";
import About from "../components/About";
import Works from "../components/Works";
import Contact from "../components/Contact";
import Footer from "../components/Footer";
import "./Admin.css";

const SECTION_KEYS = [
  "nav",
  "hero",
  "education",
  "experience",
  "about",
  "works",
  "contact",
  "footer",
];

const serializeSections = (content) =>
  SECTION_KEYS.reduce((acc, key) => {
    acc[key] = JSON.stringify(content?.[key] ?? {}, null, 2);
    return acc;
  }, {});

const parseSections = (sections) => {
  const parsed = {};

  SECTION_KEYS.forEach((key) => {
    const value = sections[key];

    if (!value || value.trim() === "") {
      parsed[key] = {};
      return;
    }

    try {
      parsed[key] = JSON.parse(value);
    } catch (error) {
      throw new Error(`${key} JSON 錯誤：${error.message}`);
    }
  });

  return parsed;
};

function AdminApp() {
  const [session, setSession] = useState(null);
  const [authMode, setAuthMode] = useState("magic");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [locale, setLocale] = useState("zh");
  const [dataByLocale, setDataByLocale] = useState({
    zh: localContent.zh,
    en: localContent.en,
  });
  const [editorByLocale, setEditorByLocale] = useState({
    zh: serializeSections(localContent.zh),
    en: serializeSections(localContent.en),
  });
  const [status, setStatus] = useState("");
  const [loading, setLoading] = useState(false);

  const hasSupabase = Boolean(supabase);

  useEffect(() => {
    if (!supabase) {
      return undefined;
    }

    supabase.auth.getSession().then(({ data }) => {
      setSession(data.session);
    });

    const {
      data: { subscription },
    } = supabase.auth.onAuthStateChange((_event, newSession) => {
      setSession(newSession);
    });

    return () => subscription.unsubscribe();
  }, []);

  const loadFromDatabase = async () => {
    if (!supabase) {
      return;
    }

    setLoading(true);
    setStatus("");

    const { data, error } = await supabase
      .from("site_content")
      .select("locale,data");

    if (error) {
      setStatus(`讀取失敗：${error.message}`);
      setLoading(false);
      return;
    }

    const next = { ...dataByLocale };

    data.forEach((row) => {
      if (row.locale && row.data) {
        next[row.locale] = row.data;
      }
    });

    setDataByLocale(next);
    setEditorByLocale({
      zh: serializeSections(next.zh),
      en: serializeSections(next.en),
    });
    setLoading(false);
    setStatus("已從資料庫載入內容");
  };

  const seedFromLocal = async () => {
    if (!supabase) {
      return;
    }

    setLoading(true);
    setStatus("");

    const payload = [
      { locale: "zh", data: localContent.zh },
      { locale: "en", data: localContent.en },
    ];

    const { error } = await supabase
      .from("site_content")
      .upsert(payload, { onConflict: "locale" });

    if (error) {
      setStatus(`寫入失敗：${error.message}`);
    } else {
      setDataByLocale({ zh: localContent.zh, en: localContent.en });
      setEditorByLocale({
        zh: serializeSections(localContent.zh),
        en: serializeSections(localContent.en),
      });
      setStatus("已用本地 JSON 覆蓋資料庫內容");
    }

    setLoading(false);
  };

  const handleSave = async () => {
    if (!supabase) {
      return;
    }

    setLoading(true);
    setStatus("");

    let parsed;

    try {
      parsed = parseSections(editorByLocale[locale]);
    } catch (error) {
      setStatus(error.message);
      setLoading(false);
      return;
    }

    const nextData = {
      ...dataByLocale[locale],
      ...parsed,
    };

    const { error } = await supabase
      .from("site_content")
      .upsert({ locale, data: nextData }, { onConflict: "locale" });

    if (error) {
      setStatus(`儲存失敗：${error.message}`);
      setLoading(false);
      return;
    }

    setDataByLocale((prev) => ({ ...prev, [locale]: nextData }));
    setStatus("已儲存到資料庫");
    setLoading(false);
  };

  const handleLogin = async (event) => {
    event.preventDefault();

    if (!supabase) {
      return;
    }

    setLoading(true);
    setStatus("");

    if (authMode === "magic") {
      const { error } = await supabase.auth.signInWithOtp({
        email,
        options: {
          emailRedirectTo: `${window.location.origin}/admin`,
        },
      });

      setLoading(false);

      if (error) {
        setStatus(`登入失敗：${error.message}`);
      } else {
        setStatus("請到信箱點擊登入連結");
      }

      return;
    }

    const { error } = await supabase.auth.signInWithPassword({
      email,
      password,
    });

    setLoading(false);

    if (error) {
      setStatus(`登入失敗：${error.message}`);
    }
  };

  const handleLogout = async () => {
    if (!supabase) {
      return;
    }

    await supabase.auth.signOut();
  };

  const previewContent = useMemo(
    () => ({ zh: dataByLocale.zh, en: dataByLocale.en }),
    [dataByLocale]
  );

  const editor = editorByLocale[locale];

  if (!hasSupabase) {
    return (
      <div className="admin-shell">
        <div className="admin-card">
          <h1>Supabase 尚未設定</h1>
          <p>請在 Vercel 設定環境變數後重新部署。</p>
          <ul>
            <li>REACT_APP_SUPABASE_URL</li>
            <li>REACT_APP_SUPABASE_ANON_KEY</li>
          </ul>
        </div>
      </div>
    );
  }

  if (!session) {
    return (
      <div className="admin-shell">
        <div className="admin-card">
          <h1>管理後台登入</h1>
          <p>請使用已建立的帳號登入。</p>
          <form className="admin-form" onSubmit={handleLogin}>
            <label>
              Email
              <input
                type="email"
                value={email}
                onChange={(event) => setEmail(event.target.value)}
                required
              />
            </label>
            {authMode === "password" && (
              <label>
                密碼
                <input
                  type="password"
                  value={password}
                  onChange={(event) => setPassword(event.target.value)}
                  required
                />
              </label>
            )}
            <div className="admin-actions">
              <button type="submit" disabled={loading}>
                {authMode === "magic" ? "寄送登入連結" : "登入"}
              </button>
              <button
                type="button"
                className="admin-secondary"
                onClick={() =>
                  setAuthMode((mode) => (mode === "magic" ? "password" : "magic"))
                }
              >
                切換到{authMode === "magic" ? "密碼" : "magic link"}
              </button>
            </div>
          </form>
          {status && <p className="admin-status">{status}</p>}
          <p className="admin-note">
            如果還沒有帳號，請到 Supabase Dashboard → Authentication → Users
            建立使用者。
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="admin-shell admin-layout">
      <aside className="admin-panel">
        <header className="admin-header">
          <h1>內容管理</h1>
          <div className="admin-toolbar">
            <button onClick={handleLogout} className="admin-secondary">
              登出
            </button>
          </div>
        </header>
        <div className="admin-controls">
          <div className="admin-locale">
            <button
              type="button"
              className={locale === "zh" ? "active" : ""}
              onClick={() => setLocale("zh")}
            >
              中文
            </button>
            <button
              type="button"
              className={locale === "en" ? "active" : ""}
              onClick={() => setLocale("en")}
            >
              English
            </button>
          </div>
          <div className="admin-toolbar">
            <button onClick={loadFromDatabase} disabled={loading}>
              重新載入
            </button>
            <button onClick={seedFromLocal} disabled={loading}>
              以本地 JSON 重置
            </button>
            <button onClick={handleSave} disabled={loading}>
              儲存目前語系
            </button>
          </div>
          {status && <p className="admin-status">{status}</p>}
        </div>
        <div className="admin-editor">
          {SECTION_KEYS.map((key) => (
            <div className="admin-section" key={key}>
              <div className="admin-section-title">{key}</div>
              <textarea
                value={editor[key]}
                onChange={(event) =>
                  setEditorByLocale((prev) => ({
                    ...prev,
                    [locale]: {
                      ...prev[locale],
                      [key]: event.target.value,
                    },
                  }))
                }
                rows={8}
              />
            </div>
          ))}
        </div>
      </aside>
      <main className="admin-preview">
        <LanguageProvider
          initialLanguage={locale}
          initialContent={previewContent}
          enableRemote={false}
        >
          <div className="admin-preview-inner">
            <Navbar />
            <main>
              <Hero />
              <Education />
              <Experience />
              <About />
              <Works />
              <Contact />
            </main>
            <Footer />
          </div>
        </LanguageProvider>
      </main>
    </div>
  );
}

export default AdminApp;
