import { createContext, useContext, useEffect, useMemo, useState } from 'react';
import localContent from '../content';
import { supabase } from '../lib/supabaseClient';

const LanguageContext = createContext(null);

const mergeDeep = (base, override) => {
  if (override === null || override === undefined) {
    return base;
  }

  if (Array.isArray(base) || Array.isArray(override)) {
    return override;
  }

  if (typeof base !== 'object' || typeof override !== 'object') {
    return override;
  }

  const result = { ...base };

  Object.keys(override).forEach((key) => {
    result[key] = mergeDeep(base?.[key], override[key]);
  });

  return result;
};

export function LanguageProvider({
  children,
  initialLanguage = 'zh',
  initialContent,
  enableRemote = true,
}) {
  const [language, setLanguage] = useState(initialLanguage);
  const [contentState, setContentState] = useState(
    initialContent || localContent
  );

  useEffect(() => {
    document.documentElement.lang = language === 'zh' ? 'zh-Hant' : 'en';
  }, [language]);

  useEffect(() => {
    if (!enableRemote || !supabase) {
      return undefined;
    }

    let isMounted = true;

    const loadContent = async () => {
      const { data, error } = await supabase
        .from('site_content')
        .select('locale,data');

      if (error || !data) {
        return;
      }

      const merged = { ...localContent };

      data.forEach((row) => {
        if (row.locale && row.data) {
          merged[row.locale] = mergeDeep(
            localContent[row.locale] || {},
            row.data
          );
        }
      });

      if (isMounted) {
        setContentState(merged);
      }
    };

    loadContent();

    return () => {
      isMounted = false;
    };
  }, [enableRemote]);

  const value = useMemo(
    () => ({
      language,
      setLanguage,
      content: contentState[language] || contentState.zh,
    }),
    [language, contentState]
  );

  return (
    <LanguageContext.Provider value={value}>{children}</LanguageContext.Provider>
  );
}

export function useLanguage() {
  const context = useContext(LanguageContext);

  if (!context) {
    throw new Error('useLanguage must be used within LanguageProvider');
  }

  return context;
}
